#include "inc.h"
//658. Find K Closest Elements My SubmissionsBack to Contest
class Solution {
public:
    vector<int> findClosestElements(vector<int>& arr, int k, int x) {
        int len = arr.size();

        //x = x < 0? arr[0]:x;
        int index = 0;
        int min = INT_MAX;
        for (int i = 0; i < len; i++) {
            if(abs(arr[i]-x) < min){
                index = i;
                min = abs(arr[i]-x);
            }
        }
        cout << index<<endl;
        int i = index;
        int j = index;
        while ((i>=0 || j < len )&&(j-i-1<k)){
            //cout << "i:"<<i<<",j:"<<j<<endl;
            if (i>=0 && abs(arr[i]-x) <= abs(arr[j]-x)){
                i--;
            }else
            if (j <len &&abs(arr[i]-x) > abs(arr[j]-x)){
                j++;
            }
            else if (i == -1&&j<len) j++;
            else if (j == len&&i>0) i--;
            // if (i>0 && j-i+1<k) i--;
            // if (j<len && j-i+1<k) j++;
            cout << "i:"<<i<<",j:"<<j<<endl;
        }
        //cout << "------------------------"<<endl;
        //cout << "i:"<<i<<",j:"<<j<<endl;
        vector<int> result;
        for (int start = i+1; start < j; start++) {
            result.push_back(arr[start]);
        }
        return result;
    }
};
